    const currentAddress = startAddress + i;

    // For each address, find a global object whose SelectedRegister matches
    for (let k = 0; k < allKeys.length; k++) {
        const key = allKeys[k];
        const dataObject = global.get(key);

        if (!dataObject) continue;

        if (dataObject.SelectedRegister === currentAddress) {
            // We found the matching data descriptor
            const dataType = (dataObject.DataType || "").toLowerCase();
            const byteOrder = (dataObject.ByteOrder || "BE").toUpperCase();

            // 4) Parse the raw data
            let rawValue;
            if (dataType === "int32" || dataType === "uint32" || dataType === "float32") {
                if (i + 1 >= quantity) {
                    node.warn(`Not enough registers to parse 32-bit at address ${currentAddress}`);
                    continue;
