 * @param {Array}  regs - e.g. [28, 12]
 * @param {String} dataType  - "int32", "uint32", or "float32"
 * @param {String} byteOrder - "LE" or "BE"
 */
function parse32Bit(regs, dataType, byteOrder) {
    if (regs.length < 2) {
        node.warn("parse32Bit expected 2 registers");
        return 0;
    }

    dataType = dataType.toLowerCase();
    byteOrder = byteOrder.toUpperCase();

    if (dataType === "int32" || dataType === "uint32") {
        let low, high;
        if (byteOrder === "LE") {
