        }
    }
    else if (dataType === "float32") {
        // Interpret as IEEE 754 float
        let buffer = new ArrayBuffer(4);
        let view = new DataView(buffer);

        if (byteOrder === "LE") {
            view.setUint16(0, regs[0], true);
