// Gather flow variables
let ByteOrder        = flow.get("ByteOrder");
let DataDimension    = flow.get("DataDimension");
let DataMultiplier   = flow.get("DataMultiplier");
let DataType         = flow.get("DataType");
let SelectedRegister = flow.get("SelectedRegister");
let ValueName        = flow.get("ValueName");

// Create a combined object
let combinedObject = {};
combinedObject[ByteOrder.topic]        = ByteOrder.payload;
combinedObject[DataDimension.topic]    = DataDimension.payload;
combinedObject[DataMultiplier.topic]   = DataMultiplier.payload;
combinedObject[DataType.topic]         = DataType.payload;
combinedObject[SelectedRegister.topic] = SelectedRegister.payload;
combinedObject[ValueName.topic]        = ValueName.payload;

// Sanitize the global variable name (replace spaces with underscores)
let safeName = ValueName.payload.replace(/\s/g, "");

// Store combined object under the sanitized name
global.set(safeName, combinedObject);

// Send the combined object as msg.payload for debugging
msg.payload = combinedObject;
return msg;
